INSERT INTO `zxjy_article` VALUES ('162', '41', '0', '诚聘英才', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<p>\r\n	<span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">&nbsp;&nbsp;&nbsp; 因业务发展需要诚聘淘宝刷单兼职或全职，如有意向请联系我们</span>\r\n</p>\r\n<p>\r\n	<span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">官方</span><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">QQ刷单群:</span><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">548324118&nbsp;&nbsp;&nbsp; 官方</span><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">QQ</span><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#003399;\">：2661108139</span>\r\n</p>', '', '', null, '1', null, '1', '1', '0', '1460835908', null, '1', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('163', '33', '0', '如何充值和提现', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '充值提现', null, '<p style=\"text-align:center;\" align=\"center\">\r\n	<span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:24px;font-family:&quot;color:#E53333;\"></span><span></span><span style=\"font-size:16.0pt;font-family:&quot;color:red;\"><span style=\"color:#E53333;font-size:24px;font-family:\'Microsoft YaHei\';\">第一步：如何充值</span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<br />\r\n<span style=\"font-size:16.0pt;font-family:&quot;color:red;\"><span style=\"color:#E53333;font-size:24px;font-family:\'Microsoft YaHei\';\"></span><span></span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#000000;\"><span style=\"color:#000000;font-size:24px;font-family:\'Microsoft YaHei\';\">1、打开会员中心</span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<br />\r\n<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#000000;\"><span style=\"color:#000000;font-size:24px;font-family:\'Microsoft YaHei\';\"></span></span><span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:18px;font-family:&quot;color:#E53333;\"><span style=\"color:#E53333;font-size:18px;\"><img src=\"/attached/image/20160417/20160417052819_53258.jpg\" alt=\"\" /><br />\r\n</span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span><br />\r\n</span><span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:red;\"><span style=\"font-size:24px;color:#000000;\">2、点击账号充值，</span><span style=\"font-family:\'Microsoft YaHei\';color:red;font-size:18px;\"><span style=\"color:#000000;font-size:24px;\">注意：充值时需要备注好您的用</span><span style=\"color:#000000;font-size:24px;\">户名</span></span><span style=\"font-size:24px;color:#000000;\">。</span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:18px;font-family:Microsoft YaHei;color:red;\"><img src=\"/attached/image/20160417/20160417053527_87662.jpg\" alt=\"\" /></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:18px;font-family:Microsoft YaHei;color:red;\"><img src=\"/attached/image/20160417/20160417053619_20978.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span><span></span><br />\r\n</span><span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;text-indent:0cm;\">\r\n	<span style=\"font-size:24px;color:#E53333;font-family:\'Microsoft YaHei\';\"><span style=\"color:#E53333;font-family:\'Microsoft YaHei\';font-size:24px;\">第二步：</span></span><span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#E53333;\">如何提现</span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;text-indent:0cm;\">\r\n	<br />\r\n<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#E53333;\"></span><span style=\"font-size:16.0pt;color:red;\"></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#000000;\">1、会员中心中打开申请提现。</span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<br />\r\n<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#000000;\"></span><span style=\"font-size:16.0pt;color:red;\"></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:16pt;font-family:Microsoft YaHei;color:#E53333;\"><img src=\"/attached/image/20160417/20160417053732_40986.jpg\" alt=\"\" /><br />\r\n</span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span><br />\r\n</span><span></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#000000;\">2、输入银行卡号和验证码，完成提现。</span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<br />\r\n<span style=\"font-size:24px;font-family:\'Microsoft YaHei\';color:#000000;\"></span><span style=\"font-size:16.0pt;color:red;\"></span> \r\n</p>\r\n<p style=\"margin-left:157.5pt;\">\r\n	<span><img src=\"data:image/png;base64,\" alt=\"\" /><br />\r\n</span><span></span> \r\n</p>\r\n<p>\r\n	<span>&nbsp;</span> \r\n</p>\r\n<br />', '', '', null, '1', null, '1', '1', '0', '1460953596', '2', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('166', '33', '0', '【重要提醒】浏览器的选择', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"font-family:Microsoft YaHei;font-size:24px;\">为更好地在平台上操作，拍淘客平台建议大家下载火狐浏览器，火狐浏览器官方下载地址：</span><a><span style=\"color:#E53333;font-family:Microsoft YaHei;font-size:24px;\">http://www.firefox.com.cn/</span></a> \r\n<p>\r\n	<a><span style=\"color:#E53333;\"><span style=\"color:#000000;\"></span><span style=\"font-family:Microsoft YaHei;font-size:24px;\">&nbsp;&nbsp;&nbsp; <span style=\"color:#000000;\">火狐浏览器</span></span><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#000000;\">具有更好的兼容性，可以兼容所有系统，且操作更流畅。</span></span></a> \r\n</p>\r\n<a><span style=\"color:#E53333;\"><span style=\"font-family:Microsoft YaHei;font-size:24px;color:#000000;\">&nbsp;&nbsp;&nbsp; 其它浏览器因为系统兼容性不强会影响会员的正常操作，切记！</span></span></a><br />', '', '', null, '1', null, '1', '1', '0', '1460953583', '1', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('167', '38', '0', '【发布方教程】怎么样让刷手好评晒图', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<strong>商家操作：在发布任务页面 - 增值服务，选择“好评晒图”。</strong><br />\r\n<strong> 消耗米粒：每张好评图片消耗约0.5米粒，具体以平台显示为准。</strong><br />\r\n<br />\r\n<img alt=\"\" src=\"http://www.milioo.com/article/uploads/allimg/160326/1-160326110234220.png\" style=\"border:0px;width:855px;height:323px;\" /><br />\r\n<br />\r\n<br />\r\n<br />\r\n<strong>下面就是刷手好评晒图后的效果，会在你的宝贝评论里面显示这样的效果。</strong><br />\r\n<br />\r\n<img alt=\"\" src=\"http://www.milioo.com/article/uploads/allimg/160326/1-16032611035R52.png\" style=\"border:0px;width:793px;height:673px;\" /> \r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460837725', '8', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('168', '39', '0', '接手任务三大需知和六大注意事项', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<h2 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:24px;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>接手任务三大需知：</strong></span> \r\n</h2>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<strong>一、</strong><strong>接手任务后，在</strong><span style=\"font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>30</strong><strong>分钟内</strong></span><strong>需要在平台完成支付流程，否则任务会自动退出。</strong> \r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<strong>&nbsp;</strong> \r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<strong>二、</strong><strong>拍淘客平台的任务基本都是垫付任务，商家已经充值资金到平台，平台已扣押了卖家任务商品款+佣金，所以可放心去淘宝拍下任务商品，完成任务后接手将获得任务的存款+佣金。</strong> \r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<strong>三、</strong><strong>拍单时一定要</strong><span style=\"font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>核对商品价格，付款价格与平台任务价格必须一致</strong></span><strong>，如不一致，请</strong><span style=\"font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>QQ联系卖家</strong></span><strong>修改</strong>。\r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	------------------------------------------------------------------------------------------------------------\r\n</p>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h2 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:24px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">接手任务六大注意事项：（情节严重者直接封号！）</span> \r\n</h2>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FFFFFF;background-color:#000000;\"><strong>【注：违反以下任意一条，根据情节处罚2-8个豆豆补偿给对方并警告，再犯者扣罚双倍，累计三次将封号处理！】</strong></span> \r\n</h3>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">一、严禁通过旺旺联系对方并带有平台、拍淘客、刷钻、刷单等忌讳字眼&nbsp;(请使用QQ联系对方)；</span> \r\n</h3>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">二：禁止在任务过程中，辱骂任务一方，出言不逊；</span> \r\n</h3>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">三、</span><span style=\"color:#FF0000;font-family:inherit;font-size:18.719999313354492px;font-style:inherit;font-weight:inherit;\">禁止使用花呗、淘金币、天猫积分、返利网、淘宝客、信用卡支付；</span> \r\n</h3>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">四、</span><span style=\"font-family:inherit;font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;vertical-align:baseline;color:#FF0000;\">必须物流信息签收后才能在淘宝确认收货！（无法获取物流信息的除外）</span><span style=\"font-family:inherit;font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;vertical-align:baseline;color:#FF0000;\">；</span> \r\n</h3>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">五、</span><span style=\"color:#FF0000;font-family:inherit;font-size:18.719999313354492px;font-style:inherit;font-weight:inherit;\">禁止以任何理由给对方差评及非五星好评等行为；</span> \r\n</h3>\r\n<p style=\"font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	&nbsp;\r\n</p>\r\n<h3 style=\"font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n	<span style=\"font-weight:inherit;font-style:inherit;font-size:18.719999313354492px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">六、</span><span style=\"color:#FF0000;font-family:inherit;font-size:18.719999313354492px;font-style:inherit;font-weight:inherit;\">严禁好评乱写评语，广告评语或恶意乱写评语。</span> \r\n</h3>', '', '', null, '1', null, '1', '1', '0', '1460838067', '2', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('169', '38', '0', '淘宝刷单怎么才能不被降权？', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong>1、刷销量时双方都不评价，或者卖号先评，买号等默认好评，这样更安全；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 2、刷销量时如果周转资金充足，尽量晚发货晚确认，或者等默认；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 3、不要在半夜三更刷，会让淘宝觉得不正常；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 4、每月有几天系统大排查，当天不刷不评；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 5、改价需要使用安全减价软件，最终成交价格不能低于30元；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 6、刷完销量之后最好有一些真实的交易，这样会更安全；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 7、刷完销量之后，当天夜里12点之前下架一次，第二天早上8点后再上架；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 8、真刷强烈推荐有流量，才能有销量，先刷流量之后才能刷销量；除非是为信用（就是删除宝贝等系类刷信用技术）</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong>9、至少要3—5天才能确认收货；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 10、卖号的支付宝不能关联买号的支付宝；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 11、刷销量时最好通过关键词搜索进入，找到宝贝；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 12、店铺和单品的转化率最好控制在2~6%；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 13、避免使用在淘宝上出现过多次的单号，网上购买的单号也慎用；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 14、刷信誉每天最好不要超过30笔,淘云端手机刷单，自己可以掌握刷单时间、刷单数量。</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> 15、买号、卖号不能在同一台电脑登陆；</strong></span><br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> &nbsp; &nbsp; 每次刷完清理下IE缓存COOKIE（专业工具：CCleaner系统垃圾清理软件）；</strong></span><br />\r\n<br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong>16、不要在旺旺上输入刷销量相关内容，</strong></span><br />\r\n<span style=\"font-size:18px;color:#337FE5;font-family:&quot;\"><strong> &nbsp; &nbsp; 比如刷单、刷销量之类的敏感关键词，容易被淘宝监控到。</strong></span>', '', '', null, '1', null, '1', '1', '0', '1460837680', '6', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('170', '39', '0', '【接手方教程】如何避免小号异常，如何培养淘宝小号？', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<span style=\"text-decoration:none;font-size:16px;\"><strong><span style=\"color:#E53333;\"><strong><span></span><span></span></strong></span></strong></span><br />\r\n<span style=\"text-decoration:none;font-size:16px;\"><strong><span style=\"color:#E53333;\"><strong>严重警示：</strong></span></strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<span style=\"text-decoration:none;font-size:16px;\"><strong><span style=\"color:#E53333;\"><strong></strong></span></strong></span><br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>请购买小号以后不要一个IP连续登录多个刚买的号，不然容易出现异常。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>登录之后没有出现问题，后续自行操作引起的限购等各种情况买家自行负责。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>切忌一定不能登录阿里旺旺。</strong></span><br />\r\n<strong>&nbsp;</strong><br />\r\n<span style=\"text-decoration:none;font-size:16px;color:#E53333;\"><strong>培养小号流程：</strong></span><br />\r\n<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>1、先通过网页登录淘宝账号，然后去浏览淘宝上面的商品，登录之后你可以看到上次登录时间。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>2、切记登录之后下次登录不要改动IP，还是要原IP登录。异地登录容易出现异常，一定要适应好IP后再使用。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>3、不要修改任何淘宝号还有资料的资料，也不要设置任何资料，等适应IP之后在操作修改。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>4、登录小号后请自行浏览淘宝随便一个店铺的宝贝拍一单提交看有没有限购（不需付款），如有限购请立即过来换。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>5、避免限购，需登陆淘宝后台，将原有收货地址删掉，改成自己ip的收货地址，然后在拍单。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>6、等适应IP之后48小时后才能修改资料。否则出现限购买家自行负责。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>一切正常之后就可以刷单了。</strong></span><br />\r\n<br />\r\n<strong>&nbsp;</strong><br />\r\n<span style=\"text-decoration:none;font-size:16px;\"><span style=\"text-decoration:none;color:#FF0000;\"><strong>红色警报：</strong></span></span><br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>所有实名刷单买家心号收到手后一定不能修改淘宝密码，支付宝登录密码支付密码还有改邮箱资料，否则出现限购情况无售后。建议使用48小时后在进行修改</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<span style=\"text-decoration:none;font-size:14px;\"><strong>&nbsp;</strong></span><br />\r\n<br />\r\n<span style=\"text-decoration:none;font-size:16px;color:#E53333;\"><strong>安全警示：</strong></span><br />\r\n<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>只有让淘宝认定你的号不是批量操作的小号才是最安全的。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>账号最好自行把资料修改齐全，设置好密保、手机、安装好证书等后在进行开始刷单。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>最好每使用一个号完成之后都清除一下上网痕迹，清理一下COOK，然后再继续使用操作下一个号。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>淘宝最近严查批量操作都容易出现异常冻结，限购等情况，请谨慎操作您的号，珍惜自己的财产。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>首次使用请谨慎操作培养你的号，否则出现异常还可以自行解除，出现冻结或限购就没办法了。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;color:#333333;font-family:\'Microsoft YaHei\', Tahoma;\">\r\n		<br />\r\n<span style=\"text-decoration:none;font-size:14px;\"><strong>如果在出现异常的情况下，解除异常之前请先一下上网痕迹，清理一下COOK，然后换IP再进行解除异常，每解除成功一个要继续解除下一个的话都必须清理上网痕迹，清理一下COOK，然后换IP再进行解除异常，不然就会出现限购情况，这些情况都是自己操作不当造成。</strong></span> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838285', '8', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('171', '39', '0', '更换IP方法详解', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<h3 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-size:18.72px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">1、为什么在刷钻的时候要换ip？</span> \r\n	</h3>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>如果我们不换ip，淘宝后台会记录这个ip所登录过的号码（小号），一旦发现，必定会对这些号码做出惩罚。</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<h3 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-size:18.72px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">2、换ip有哪些方法？</span> \r\n	</h3>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>1）</strong></span><strong>目前换ip可以通过反复拨号来获取新的ip地址</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>2）</strong></span><strong>利用vpn工具来切换ip（买VPN来换IP其实很方便也很安全，一个月的花费也就20元左右，视个人经济情况而定）</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<h3 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-size:18.72px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">3、我们这里重点介绍的是免费换IP方法（此方法对单位固定IP不管用）</span> \r\n	</h3>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>1）</strong></span><strong>没有安装路由器（或者无线路由）的情况下，通过电脑拨号来获取新的ip地址：</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>我们的网络供应商在给我们装好宽带之后，会有提供帐号和密码给你，你使用这个帐号和密码连网一次，即属于拨号成功一次，也就是换一次ip。你可以先断网，然后等10秒钟重新连接，一般即可达到换IP的目的。</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>2）</strong></span><strong>安装了路由器或者无线路由的情况下，需要通过重启路由或者断开路由电源来获取新的ip</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<h3 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-size:18.72px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">4、怎么知道ip换掉了没有？</span> \r\n	</h3>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>你可以打开</strong><a href=\"http://www.ip138.com/\" target=\"blank\"><strong>http://www.ip138.com/</strong></a><strong>&nbsp;看一下你的ip地址，然后断网重拨，再次打开</strong><a href=\"http://www.ip138.com/\" target=\"blank\"><strong>http://www.ip138.com/</strong></a><strong>看一下ip变了没有，如果变了，就是换ip成功。</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<h3 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-size:18.72px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">5、为什么说不要登录旺旺？</span> \r\n	</h3>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>旺旺可以把你的硬件信息全部记录，就像很多软件可以测出你的电脑配置和型号一样，通常都是记录mac地址。如果你不想自己的小号出问题，就不要登录旺旺！切记！</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>另外还有很多换IP的免费软件，其中有专门给路由用户换IP的，大家可以百度一下“换IP软件”会找到很多这样的软件，比你自己手动换IP要轻松得多。</strong> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838353', '11', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('172', '39', '0', '货比三家教程', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>货比三家：根据商家给出的关键词进行搜索，然后浏览相关的宝贝并进行截图上传。（商家可选货比1-3家，根据要求完成任务）</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\">第一步</span>：以“苹果手机壳”为例，在淘宝搜索此关键词。\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<img alt=\"00155ac730f09574ac84d07037379e9\" src=\"http://help.milioo.com/attachments/download/163226/00155ac730f09574ac84d07037379e9\" style=\"border:0px;height:auto !important;\" /> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\">第二步</span>：搜索结果如下图1，假设红色方框的为商家所发布的商品，那么除去这个商品，其余就是货比三家的商品了\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		点进去并停留至少1分钟，并截取宝贝图片进行上传，如下图2\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		图1\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<img alt=\"00155ac75403b004e8597410205fe3d\" src=\"http://help.milioo.com/attachments/download/163245/00155ac75403b004e8597410205fe3d\" style=\"border:0px;height:auto !important;\" /> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		图2:\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<img alt=\"00155ac7555e977448e5aa45336b3aa\" src=\"http://help.milioo.com/attachments/download/163246/00155ac7555e977448e5aa45336b3aa\" style=\"border:0px;height:auto !important;\" /> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838370', '12', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('173', '39', '0', '如何屏蔽淘宝客', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>作为一个威客，有时候会莫名其妙的被商家投诉您是通过淘宝客链接进店购物的，给其造成了损失，商家投诉成功后，是需要威客为此产生的佣金埋单的。这样的结果，使得双方都蒙受损失。</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<strong>可能你并没有故意去这样做。那么，我们要如何屏蔽淘宝客呢？</strong> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\">屏蔽淘宝客的方法：</span>http://jingyan.baidu.com/article/adc81513728df7f723bf73ea.html\r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838504', '17', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('174', '39', '0', '如何彻底清除干净cookies？', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		接发任务时候要经常清除cookies！特别是切换淘宝账号时候，一定要清除cookies（每登陆小号之前或者换小号之前一定必须清除一次cookies）。经常清除cookies，一天分开时间段刷40.50个任务也是非常安全的。<br />\r\n<br />\r\n您在登录淘宝买号之前，一定必须谨记四个流程：<br />\r\n一：先把电脑上登录着的旺旺和浏览器关掉（旺旺不关，直接监控你的浏览器登录过的号），<br />\r\n二：用IE工具选项或360清除干净cookies(注意:清理cookies之前，必须关闭浏览器，否则cookies是无法清理掉)<br />\r\n三：如果有条件的话，就要<a href=\"http://help.milioo.com/posts/view/52449\" target=\"blank\">更换IP地址</a>，这样才更加安全！<br />\r\n四：如果再一次登录淘宝买号，请重复以上3个步骤即可！<br />\r\n&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\">IE清除cookies方法：</span><a href=\"http://jingyan.baidu.com/article/90808022afca8efd91c80fcc.html\" target=\"blank\">http://jingyan.baidu.com/article/90808022afca8efd91c80fcc.html</a> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838493', '16', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('175', '39', '0', '淘宝刷单网银付款退款去向规则', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<h2 style=\"text-decoration:none;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;text-align:center;text-indent:0px;background-color:#FFFFFF;\">\r\n	<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			<span style=\"color:#E53333;\"><strong>很多新手可能还是不知道使用网银支付退款到底是退到哪里，给大家普及下淘宝网银付款退款规则！</strong></span> \r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			&nbsp;\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			1.如果威客是在确认收货前退款，退款将会退回到付款的网银。\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			&nbsp;\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			2.如果刷手确认收货后，仅仅申请退款不退货，退款将会退回到到付款的网银。\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			&nbsp;\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			3.如果刷手是确认收货后，申请退货退款，退款将会退回到退回到刷手的支付宝。\r\n		</p>\r\n	</div>\r\n<br />\r\n</h2>', '', '', null, '1', null, '1', '1', '0', '1460838467', '15', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('176', '39', '0', '如何更加安全的使用小号(买号)？', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;color:#FF0000;\">注册时注意事项：</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">1、注册新的小号时千万不要一个IP注册超过三个小号，每注册两三个就要换IP和清cookies！各位 ，切记切记。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">2、注册新的小号，尽量不要使用同一个开头，然后后面跟一串排好的数字，这样做无疑是等于告诉淘宝您要准备干嘛来了。（本人碰到的最傻的买号就是 shua001，shua002，shua003...... ）</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">3、尽量使用中文用户名，经过简单化长期的调查发现英文小号的冻结几率比中文小号至少要高出50%</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">4、千万不要用系列或者雷同邮箱名，同时有一点很重要就是尽量不要用yahoo邮箱，我想大家都知道yahoo跟淘宝是一个大东家吧，除非您愿意注册邮箱时候也不停的换IP和清cookies！</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;color:#FF0000;\">在使用小号时请务必注意一下几项哦：</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">第一：经常轮换使用小号， 平台规定每个小号每天可接手任务数为6个，这并不意味着，您可以一连串用同一个小号不停的购买不同的任务商品六次。试想一下有哪个真正的买家，刚刚注册不久就每天六个每天六个的不停买东西呢？所以我建议大家还是轮换着用不同的小号去执行任务哦，而且尽量不要买满六个。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">第二：切换小号登陆淘宝之前清空浏览器的cookies和重启一下您的（猫）切换IP（拍下和确认收货时都要注意），因为淘宝会利用cookies记录很多您上次登录的账号信息的，如果经常在同一台电脑切换小号登陆，淘宝系统就会记录下来并且自动将这几个小号关联起来，到时候如果一个小号出了问题，恐怕其他小号也小命不保了哦。至于清空cookies和重启（猫）的方法我这里就不赘述了哦，有不明白的可以咨询我们简单化的客服妹妹哦。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">第三：小号挂旺旺现象，在接任务的时候一些接手方会把淘宝小号的旺旺开着，殊不知您的旺旺已经彻底出卖了您。基于网页的程序，只能判断您的ip，没有系统权限获得更多的资料，但是作为安装在系统内的旺旺软件，却可以把您的所有一切都反馈给淘宝，包括您的cpu序列号、硬盘序列号、网卡物理地址、网络环境、路由器序列号、同局域网的其他电脑的mac地址等等。有了这个“奸细”在，淘宝要查买号是轻而易举的。如此对这些买号接手的任务也存在很大的被自查的安全隐患。所以请接手任务，非聊天任务的请不要用旺旺登录买号。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">第四：针对淘宝IP检测，短时间内不要跨省归属地换IP。支付宝的IP地址适应程序已经包装成模块在这里共用，凡事短时间内频繁跨省跳转的均会被列入重点监控。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">第五：不要频繁修改收货地址； 一个正常的买号不会频繁修改收货地址，大家在接手需要修改收货地址的任务是，尽量分散开买号绑定，做到一周内改地址不超过一次！</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;font-size:14px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n		<span style=\"text-decoration:none;font-style:inherit;font-family:微软雅黑, \'Microsoft YaHei\';vertical-align:baseline;\">第六：就是每个买号在启用时，请务必到淘宝上完善一下该买号的个人信息哦，这一点很关键，但是也最容易被忽视，淘宝前一段时间已经清理过一批这样的买号了哦，当然不必要填写真实的个人信息，只要看起来像个人名和地址就好了。各位，切记切记。</span> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838440', '14', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('177', '39', '0', '完成任务，对方不放款解决方法', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<span style=\"text-decoration:none;color:#444444;font-family:微软雅黑;font-size:16px;line-height:30px;\">完成任务,并且已经在淘宝确认收货,最快的方法是</span><span style=\"text-decoration:none;color:#FF0000;\"><span style=\"text-decoration:none;font-family:微软雅黑;font-size:16px;line-height:30px;\">QQ联系</span></span><span style=\"text-decoration:none;color:#444444;font-family:微软雅黑;font-size:16px;line-height:30px;\">发布方回到平台放款。<br />\r\n<br />\r\n但是由于商家可能不在电脑前或有事耽搁不能及时放款，那么您可以选择如下操作。</span><br />\r\n<br />\r\n<strong>请点击此任务中的“<span style=\"text-decoration:none;color:#FF0000;\">申诉</span>”按钮</strong><span style=\"text-decoration:none;color:#444444;font-family:微软雅黑;font-size:16px;line-height:30px;\">，</span><span style=\"text-decoration:none;color:#444444;font-family:微软雅黑;font-size:16px;line-height:30px;\">提交您在</span><span style=\"text-decoration:none;color:#FF0000;\"><span style=\"text-decoration:none;font-family:微软雅黑;font-size:16px;line-height:30px;\"><strong>淘宝确认收货与好评的截图</strong></span></span><span style=\"text-decoration:none;color:#444444;font-family:微软雅黑;font-size:16px;line-height:30px;\">，我们将在</span><span style=\"text-decoration:none;color:#FF0000;\"><span style=\"text-decoration:none;font-family:微软雅黑;font-size:16px;line-height:30px;\">24小时内</span></span><span style=\"text-decoration:none;color:#444444;font-family:微软雅黑;font-size:16px;line-height:30px;\">为您处理.提交申诉后请您耐心等待。</span> \r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838337', '10', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('178', '39', '0', '手机订单任务教程', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<h2 style=\"text-decoration:none;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;text-align:center;text-indent:0px;background-color:#FFFFFF;\">\r\n	<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			<strong><span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><span></span>手机订单:&nbsp;</span></strong>就是接手人需要用智能手机（或pad）的淘宝应用程序登陆绑定的买号完成购买、浏览、支付等任务规定的行为；\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong><span style=\"text-decoration:none;color:#FF0000;\">发布手机任务</span><strong><span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\">:</span></strong></strong>勾选增值服务中的“手机订单”即可。<br />\r\n<img alt=\"\" src=\"http://www.milioo.com/article/uploads/allimg/160109/1-1601091019441D.jpg\" style=\"border:0px;width:1035px;height:415px;\" /> \r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			&nbsp;\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			&nbsp;<span></span><strong><span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\">接手任务前需要具备如下条件：</span></strong> \r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			1. 拥有一个使用苹果系统或者安卓系统的智能手机或者平板电脑<br />\r\n2.在智能机或者平板电脑上安装了淘宝应用，与旺信应用<br />\r\n<br />\r\n<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span style=\"text-decoration:none;font-style:inherit;font-family:inherit;vertical-align:baseline;color:#FF0000;\"><strong>操作流程：</strong></span> \r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			使用手机或平板电脑的淘宝app登陆任务绑定的买号，完成任务前置操作后下单付款，以卖家已卖出宝贝交易中显示【手机订单】为检测标准\r\n		</p>\r\n		<p style=\"text-decoration:none;text-indent:2em;font-size:16px;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			&nbsp;\r\n		</p>\r\n		<h2 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			<span style=\"text-decoration:none;font-weight:inherit;font-style:inherit;font-size:24px;font-family:inherit;vertical-align:baseline;color:#FF0000;\">&nbsp;&nbsp;&nbsp;&nbsp; 重要：来路、店内浏览、浏览到底、货比如何做？</span> \r\n		</h2>\r\n		<h3 style=\"text-decoration:none;font-family:微软雅黑;vertical-align:baseline;color:#444444;\">\r\n			<strong>答：由于手机app操作暂时不如电脑便捷，而且在上传截图等操作上如果使用手机截图后倒入电脑也较为麻烦，所以平台默认规定如上操作都在电脑上操作完成即可，然后可用手机扫描二维码进入购买；如果有发布人有特殊要求一定要在手机中操作上述功能的，可以在搜索提示中说明，而且必须发布审核任务，在审核任务前获得任务双方的同意，方可作为日后申诉依据。</strong> \r\n		</h3>\r\n	</div>\r\n<br />\r\n</h2>', '', '', null, '1', null, '1', '1', '0', '1460838316', '9', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('179', '39', '0', '拍淘客“好评内容”详细解释', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:2em;\">\r\n		<span style=\"text-decoration:none;font-size:16px;color:#337FE5;\"><strong>一、带字好评，如果发布人选了\"好评内容\"，但是好评内容为空，则代表接手方根据产品自由发挥组织好评文字，不懂的可参考查询同类型产品的评价。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;\">\r\n		<span style=\"color:#337FE5;\"><strong>&nbsp; &nbsp; &nbsp; &nbsp;</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;\">\r\n		<span style=\"text-decoration:none;font-size:16px;color:#337FE5;\"><strong>二：</strong></span><span style=\"text-decoration:none;font-size:16px;color:#337FE5;\"><strong>带字好评，如果发布人选了\"好评内容\"，并且发布方写好了评语，按照发布方提供的评语评价即可。</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;\">\r\n		<span style=\"color:#337FE5;\"><strong>&nbsp;</strong></span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:2em;\">\r\n		<span style=\"text-decoration:none;font-size:16px;color:#337FE5;\"><strong>三：不带字好评，如果发布人未选择</strong></span><span style=\"text-decoration:none;font-size:16px;color:#337FE5;\"><strong>\"好评内容\"，则不需要评价。</strong></span> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838426', '13', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('180', '39', '0', '为什么我淘宝付款了，平台任务却消失了', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		<span style=\"text-decoration:none;font-size:14px;\">许多在我们<span style=\"color:#E53333;\">拍淘客刷单平台</span>做任务的时候，会遇到一些匪夷所思的问题，自己不知道到底怎么回事。今天，小编就来说一个比较匪夷所思的问题。为什么在我们刷信誉平台接了任务后，我把钱付给卖家了，但是回到平台后台的时候，却发现刚才接的那个任务却不见了？</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		<span style=\"text-decoration:none;font-size:14px;\">首先，小编在这里要告诉你，出现这个情况，不是因为别的，或者说是平台漏洞，这完全都是因为你没有按照正确的步骤来操作任务。当你接了任务并选择你想要用的淘宝帐号后，目前的状态是：“等待您付款”，而这个状态的时间是30分钟，假如30分钟到了，那么这个属于你的任务就会自动取消接手状态，重新回到任务大厅，这个任务就有可能被别人接走，从而造成任务消失。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		<span style=\"text-decoration:none;font-size:14px;\">综上所述，可见今天说的这个任务不见了的原因就是因为这个导致的。在30分钟内，你如果付完款了，却并未点击确认付款的按钮，那么你的任务状态不会进入下一步，而是变成不是你的任务。</span> \r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		&nbsp;\r\n	</p>\r\n	<p style=\"text-decoration:none;text-indent:26px;color:#000000;font-family:\'Microsoft YaHei\', tahoma, sans-serif;font-size:13px;\">\r\n		<strong><span style=\"text-decoration:none;font-size:14px;\"><span style=\"text-decoration:none;color:#FF0000;\">解决方法</span>：通过QQ联系卖家（<span style=\"text-decoration:none;color:#FF0000;\">如若不知对方QQ可向客服提供掌柜名查询，切勿旺旺联系！！！</span>），向卖家说明你的实际情况，然后，你再去接一次这个任务（前提该任务并未被其他人接手，假如被接手了，那么联系卖家重新发布一下任务）。</span></strong> \r\n	</p>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460838248', '7', '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('181', '41', '0', '任务违规操作警告或封号名单', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<div style=\"padding:0px;margin:0px;border:none;text-decoration:none;\">\r\n		<span style=\"text-decoration:none;font-size:16px;\"><strong>平台用户日益增多，即日起将不规范行为账号进行公示，欢迎检举，针对违规用户进行警告或封停，请严格遵守接手<span style=\"color:#E53333;\">六大注意事项</span><span style=\"color:#E53333;\">。</span><br />\r\n如果您还不了解我们的平台任务流程，可查看新手帮助教程：<span style=\"color:#E53333;\">如何接任务</span>，还需了解<span style=\"color:#E53333;\">任务图标功能</span>，或者查看我们的<span style=\"color:#E53333;\">新手常见问题解答</span>。<span></span></strong></span>\r\n	</div>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460835071', null, '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
INSERT INTO `zxjy_article` VALUES ('182', '41', '0', '谷得网跑路，警告大家，请大家不要再被骗！', '+', '0', '0', null, '0', '0', '0', '0', '0', '0', '0', '1', '', null, '<div style=\"padding:0px 0px 30px;margin:0px auto;border:none;text-decoration:none;font-size:16px;color:#333333;font-family:微软雅黑, 宋体, Arial, sans-serif;font-style:normal;font-weight:normal;text-align:start;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#337FE5;\">本平台也接到许多会员说被谷得网骗了多少钱云云。</span></strong><br />\r\n<strong><span style=\"color:#337FE5;\">之前已经提醒了大家谷得网长达三个月无法提现，至今如此。</span></strong><br />\r\n<strong><span style=\"color:#337FE5;\">已经能确定是要跑路了，但网站还一直没有关闭，圈钱之用！</span></strong><br />\r\n<strong><span style=\"color:#337FE5;\">请广大朋友知悉，不要再上当受骗。周边有朋友的都告知一下。</span></strong><br />\r\n<span style=\"color:#EE33EE;\"><strong>拍淘客</strong></span><strong><span style=\"color:#337FE5;\">作为</span></strong><strong><span style=\"color:#337FE5;\">一个有实力的</span></strong><span style=\"color:#EE33EE;\"><strong>淘宝刷单平台</strong></span><strong><span style=\"color:#337FE5;\">，坚持做自己的东西，会一直陪伴大家，直至淘宝成为历史！</span></strong><br />\r\n<span></span><strong><span style=\"color:#337FE5;\">选平台请认准有企业资质、有实力、有诚信、正规化的平台，让我们大家诚信经营，一起前进！</span></strong>\r\n</div>', '', '', null, '1', null, '1', '1', '0', '1460835242', null, '0', '0', '0', '0', '0', '0', '0', '0', null, '0');
